ruleSearch.bookList:
ul.txt1@li
<js>
list = result.toArray();
L = [];
for(i in list) {
	kind = String(list[i].select("span.time").text()).replace(/\[|]/g,"");
	href = list[i].select("a").attr("href");
	URL = String(href).replace(/wap_/,"");
	html = String(java.ajax(URL));
	java.setContent(html);
	name = java.getElement(".path@a.-1").text();
	author = java.getElement("p.a@span@a").text();
	L.push({
		name: name,
		author: author,
		kind: kind,
		bookurl: URL
		});
	}
L
</js>

