ruleExplore.lastChapter:
$.lastChapterId
<js>
lastChapterName = java.getString("$.lastChapterName");
lastChapterName = lastChapterName.replaceAll("[【]?第\\s?\\d+\\s?章[】]?\\s*|[第]?[〇零一二三四五六七八九十百千万两□]+章\\s*|(?=^)\\d+(?=$)", "");
lastChapterName = lastChapterName.isEmpty() ? "" : " " + lastChapterName;
result = result==""?"":"第"+result+"章"+lastChapterName
</js>

ruleToc.updateTime:
$.chapterintro##(?![A-Za-z0-9]+)\s*(?![A-Za-z0-9]+)
<js>
java.put("chapterintro",result);
chapterintro = result;
chapterdate = java.getString("$.chapterdate");
chaptersize = java.getString("$.chaptersize");
chaptertype = java.getString("$.chaptertype");
ChapterInfo = chaptertype=="0"?chapterdate+'　'+chaptersize+'字'+'　'+chapterintro:""
</js>

ruleToc.chapterUrl:
td@a@href
<js>
novelid = result.match(/onebook.php.novelid=(\d+)/)[1];
isVolume = java.getString("td[colspan='7']@b@text");
url1 = "https://app.jjwxc.org/androidapi/novelbasicinfo?novelId="+novelid;
url = isVolume==""?url1:""
</js>

ruleToc.updateTime:
td[height='20'][align='center']@text##^(?=\S)|\n(?=\S)##🏷️
<js>
wordcount = java.getString("td[height][align='right']@text");
time = java.getString("td[title]@text");
if(wordcount > 9999) {
	wordcount = String(Math.round(wordcount/100)/100)+'万字';
	} else {
	wordcount = wordcount+'字';
	};
result+'　'+wordcount+'　'+time
</js>

